    <script type="text/javascript">
        let csrfToken = '{{ csrf_token() }}';

        $(function() {
            let socket = io('http://0.0.0.0:2021');
            socket.on('data.updated', function (response) {
                response = JSON.parse(response);
                $('#valVoltage').html(response.volatge);
                $('#valCurrent').html(response.current);
                $('#valPower').html(response.power);
                $('#valEnergy').html(response.energy);
                $('#valTemperature').html(response.temperature);
            });

            $("#stateButton").click(function (e) {
                e.preventDefault();

                let state = $(this).attr('data-state');

                $.post('/sensor/update', { state : state, _token : window.csrfToken }, function (response) {
                    let stateButton = $("#stateButton");
                    let butSate = stateButton.attr('data-state');
                    let newState = "off";
                    if (butSate === "off") {
                        newState = "on";
                        stateButton.removeClass('btn-danger').addClass('btn-success');
                        stateButton.html("Turn On");
                    } else {
                        stateButton.removeClass('btn-success').addClass('btn-danger');
                        stateButton.html("Turn Off");
                    }

                    stateButton.attr('data-state', newState);
                });
            });
            // button.change(function () {
            //     let butState = $(this).val();
            //
            //     // console.log(butState);
            //     //
            //     // if (butState === 'on') { finalState = 'off'; }
            //     // else if (butState === 'off') { finalState = 'on'; }
            //     // else {
            //     //   finalState = 'off';
            //     // }
            //
            //
            // });

            window.setInterval(function() {
                $.get('/sensor/{{ $deviceId }}/value', function(response) {
                  $('#valVoltage').html(response.voltage);
                  $('#valCurrent').html(response.current);
                  $('#valPower').html(response.power);
                  $('#valEnergy').html(response.energy);
                  $('#valTemperature').html(response.temperature);

                  var state = response.state === 'on' ? 'selected' : '';

                  $("#customToggle1").prop({checked : response.state === 'on' });
                });
            }, 1000)
        });

    </script>
